package com.e.learning;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {
    private RecyclerView contactRecView;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        contactRecView = findViewById(R.id.contactRecView);
        ArrayList<Contact> contacts = new ArrayList<>();
        contacts.add(new Contact("Allan Mesho", "mesh@gmail.com", "data:image/jpeg;base64,"));
        contacts.add(new Contact("Allanevim K", "phoyoo@gmail.com", "data:image/jpeg;base64,"));
        contacts.add(new Contact("Allan Owino", "owino@gmail.com", "data:image/jpeg;base64,"));
        contacts.add(new Contact("Lameck Meshack", "Otieno@gmail.com", "data:image/jpeg;base64,"));

        ContactRecViewAdapter adapter = new ContactRecViewAdapter(this);
        adapter.setContacts(contacts);

        contactRecView.setAdapter(adapter);
        contactRecView.setLayoutManager(new GridLayoutManager(this, 2));

    }
}